// Process form input and build Elasticsearch query for bank transactions
const formData = $input.first().json;

// Extract form values
const minAmount = formData['Minimum Amount ($)'] || 500;
const dateRange = formData['Date Range'] || '2023-04-01 to 2023-12-31 (Full Range)';
const customStartDate = formData['Start Date (if Custom Range)'];
const customEndDate = formData['End Date (if Custom Range)'];
const accountId = formData['Account ID (Optional)']?.trim() || null;
const transactionType = formData['Transaction Type'] || 'All Types';
const location = formData['Location Filter (Optional)']?.trim() || null;
const reportFormat = formData['Report Format'] || 'Text Report';

// Convert date range to start and end dates
let startDate, endDate;

if (dateRange === 'Custom Range' && customStartDate && customEndDate) {
  startDate = customStartDate + ' 00:00:00';
  endDate = customEndDate + ' 23:59:59';
} else {
  // Parse predefined ranges
  switch(dateRange) {
    case '2023-04-01 to 2023-06-30 (Q2 2023)':
      startDate = '2023-04-01 00:00:00';
      endDate = '2023-06-30 23:59:59';
      break;
    case '2023-07-01 to 2023-09-30 (Q3 2023)':
      startDate = '2023-07-01 00:00:00';
      endDate = '2023-09-30 23:59:59';
      break;
    case '2023-10-01 to 2023-12-31 (Q4 2023)':
      startDate = '2023-10-01 00:00:00';
      endDate = '2023-12-31 23:59:59';
      break;
    default: // Full Range
      startDate = '2023-04-01 00:00:00';
      endDate = '2023-12-31 23:59:59';
  }
}

// Build Elasticsearch query
const mustConditions = [
  {
    "range": {
      "TransactionDate.keyword": {
        "gte": startDate,
        "lte": endDate
      }
    }
  },
  {
    "range": {
      "TransactionAmount": {
        "gte": minAmount
      }
    }
  }
];

// Add account filter if provided
if (accountId) {
  mustConditions.push({
    "term": {
      "AccountID.keyword": accountId
    }
  });
}

// Add transaction type filter if not "All Types"
if (transactionType !== 'All Types') {
  mustConditions.push({
    "term": {
      "TransactionType.keyword": transactionType
    }
  });
}

// Add location filter if provided
if (location) {
  mustConditions.push({
    "term": {
      "Location.keyword": location
    }
  });
}

const esQuery = {
  "query": {
    "bool": {
      "must": mustConditions
    }
  },
  "sort": [{ "TransactionDate.keyword": { "order": "desc" } }],
  "size": 100
};

return {
  elasticsearchQuery: esQuery,
  searchParams: {
    minAmount,
    dateRange,
    startDate,
    endDate,
    accountId,
    transactionType,
    location,
    reportFormat
  }
};

// Get data from previous nodes
const esResponse = $input.first().json;
const searchParams = $('Build Search Query').first().json.searchParams;

// Extract results
const hits = esResponse.hits?.hits || [];
const totalFound = esResponse.hits?.total?.value || 0;

// Generate filename
const timestamp = new Date().toISOString().split('T')[0];
const isCSV = searchParams.reportFormat === 'CSV Export';
const filename = `bank_fraud_report_${timestamp}.${isCSV ? 'csv' : 'txt'}`;
const mimeType = isCSV ? 'text/csv' : 'text/plain';

// Generate report content
let reportContent = '';

if (isCSV) {
  // CSV format with all relevant fields
  reportContent = 'TransactionID,AccountID,TransactionAmount,TransactionDate,TransactionType,Location,DeviceID,IP_Address,MerchantID,Channel,CustomerAge,CustomerOccupation,TransactionDuration,LoginAttempts,AccountBalance\n';
  hits.forEach(hit => {
    const t = hit._source || {};
    reportContent += `"${t.TransactionID || ''}","${t.AccountID || ''}",${t.TransactionAmount || 0},"${t.TransactionDate || ''}","${t.TransactionType || ''}","${t.Location || ''}","${t.DeviceID || ''}","${t['IP Address'] || ''}","${t.MerchantID || ''}","${t.Channel || ''}",${t.CustomerAge || ''},"${t.CustomerOccupation || ''}",${t.TransactionDuration || ''},${t.LoginAttempts || ''},${t.AccountBalance || ''}\n`;
  });
} else {
  // Text format
  reportContent = `BANK FRAUD DETECTION REPORT\n`;
  reportContent += `==============================\n\n`;
  reportContent += `Search Criteria:\n`;
  reportContent += `- Minimum Amount: $${searchParams.minAmount}\n`;
  reportContent += `- Date Range: ${searchParams.dateRange}\n`;
  reportContent += `- Period: ${searchParams.startDate} to ${searchParams.endDate}\n`;
  reportContent += `- Account ID: ${searchParams.accountId || 'All'}\n`;
  reportContent += `- Transaction Type: ${searchParams.transactionType}\n`;
  reportContent += `- Location: ${searchParams.location || 'All'}\n\n`;
  reportContent += `Results: ${totalFound} suspicious transactions found\n\n`;

  if (hits.length > 0) {
    reportContent += `SUSPICIOUS TRANSACTIONS:\n`;
    reportContent += `========================\n\n`;
    hits.forEach((hit, index) => {
      const t = hit._source || {};
      reportContent += `${index + 1}. Transaction ID: ${t.TransactionID}\n`;
      reportContent += `   Account: ${t.AccountID}\n`;
      reportContent += `   Amount: $${t.TransactionAmount}\n`;
      reportContent += `   Date: ${t.TransactionDate}\n`;
      reportContent += `   Type: ${t.TransactionType}\n`;
      reportContent += `   Location: ${t.Location}\n`;
      reportContent += `   Channel: ${t.Channel}\n`;
      reportContent += `   Customer Age: ${t.CustomerAge}\n`;
      reportContent += `   Occupation: ${t.CustomerOccupation}\n`;
      reportContent += `   Device ID: ${t.DeviceID}\n`;
      reportContent += `   IP Address: ${t['IP Address']}\n`;
      reportContent += `   Merchant: ${t.MerchantID}\n`;
      reportContent += `   Duration: ${t.TransactionDuration}s\n`;
      reportContent += `   Login Attempts: ${t.LoginAttempts}\n`;
      reportContent += `   Account Balance: $${t.AccountBalance}\n\n`;
    });
  } else {
    reportContent += `No suspicious transactions found matching your criteria.\n`;
  }

  // Add summary statistics
  if (hits.length > 0) {
    const amounts = hits.map(hit => hit._source?.TransactionAmount || 0);
    const totalAmount = amounts.reduce((sum, amount) => sum + amount, 0);
    const avgAmount = totalAmount / amounts.length;
    const maxAmount = Math.max(...amounts);
    const minAmount = Math.min(...amounts);

    reportContent += `SUMMARY STATISTICS:\n`;
    reportContent += `===================\n`;
    reportContent += `Total Amount: $${totalAmount.toFixed(2)}\n`;
    reportContent += `Average Amount: $${avgAmount.toFixed(2)}\n`;
    reportContent += `Highest Amount: $${maxAmount.toFixed(2)}\n`;
    reportContent += `Lowest Amount: $${minAmount.toFixed(2)}\n`;
  }
}

// Convert content to binary data
const binaryData = Buffer.from(reportContent, 'utf8');

return [{
  json: {
    filename: filename,
    mimeType: mimeType,
    content: reportContent,
    totalFound: totalFound
  },
  binary: {
    data: {
      data: Buffer.from(reportContent, 'utf8'),
      mimeType: mimeType,
      fileName: filename,
      fileExtension: isCSV ? 'csv' : 'txt'
    }
  }
}];
